<script>
import gsap from "gsap";
import TheServicesHero from "../../components/The/TheServicesHero.vue";
import TheServicesList from "../../components/The/TheServicesList.vue";
import TheServicesSlider from "../../components/The/TheServicesSlider.vue";
import TheServicesDiscount from "../../components/The/TheServicesDiscount.vue";
import TheServicesCourses from "../../components/The/TheServicesCourses.vue";
import TheServicesQr from "../../components/The/TheServicesQr.vue";
import slugs from "../../data/slugs";

export default {
  components: {
    TheServicesHero,
    TheServicesList,
    TheServicesSlider,
    TheServicesDiscount,
    TheServicesCourses,
    TheServicesQr,
  },

  data: () => {
    return {
      page: null,
    };
  },

  async fetch() {
    this.page = slugs.find((el) => el.type === this.slug);
  },

  computed: {
    slug() {
      return this.$route.params.slug;
    },
  },

  async mounted() {
    const { ScrollTrigger } = await import("gsap/ScrollTrigger");
    const { DrawSVGPlugin } = await import("gsap/DrawSVGPlugin");
    gsap.registerPlugin(ScrollTrigger);
    gsap.registerPlugin(DrawSVGPlugin);
    gsap.set(".slug__decor svg path", {
      drawSVG: "0% 0%",
    });
    gsap.set([".slug__title-2", ".the-services-courses"], {
      opacity: 0,
      pointerEvents: "none",
      y: 60,
    });
    gsap.to(".slug__wrapper", {
      opacity: 1,
      duration: 0.5,
    });

    gsap.set(
      [".the-services-list", ".the-services-slider", ".the-services-qr"],
      {
        opacity: 0,
        y: "30vh",
        pointerEvents: "none",
      },
    );

    gsap.set(".the-services-list__kv", {
      left: "-50vw",
    });

    gsap.set(".the-services-discount", {
      opacity: 0,
      pointerEvents: "none",
    });

    gsap.set(".the-services-discount__kv", {
      x: "-50vw",
      scale: 0.5,
    });

    gsap.set(".the-services-discount__cloud", {
      x: "20vw",
    });

    const tlDecor = gsap.timeline({
      repeat: -1,
    });

    tlDecor.to(".slug__decor svg path", {
      drawSVG: "100% 0%",
      duration: 2,
    });
    tlDecor.to(".slug__decor svg path", {
      drawSVG: "0% 0%",
      duration: 2,
    });

    const tl = gsap.timeline({
      scrollTrigger: {
        trigger: ".slug__pin",
